
import { GokiteAASDK, UserOperationRequest, SignFunction } from 'gokite-aa-sdk';
import { ethers, Wallet } from 'ethers';
import dotenv from 'dotenv';
import fs from 'fs';

dotenv.config();

// Configuration
const KITE_CONFIG = {
  network: 'kite_testnet',
  rpc: process.env.KITE_RPC || 'https://rpc-testnet.gokite.ai',
  bundler: process.env.KITE_BUNDLER || 'https://bundler-service.staging.gokite.ai/rpc/',
  settlementToken: '0x0fF5393387ad2f9f691FD6Fd28e07E3969e27e63',
  clientAgentVaultImpl: '0xB5AAFCC6DD4DFc2B80fb8BCcf406E1a2Fd559e23',
};

// Helpers
function getTransparentProxyBytecode(): string {
  return '';
}

async function main() {
  const privateKey = process.env.CUSTODY_PRIVATE_KEY;
  if (!privateKey) throw new Error('CUSTODY_PRIVATE_KEY missing');

  const sdk = new GokiteAASDK(
    KITE_CONFIG.network,
    KITE_CONFIG.rpc,
    KITE_CONFIG.bundler
  );

  const provider = new ethers.JsonRpcProvider(KITE_CONFIG.rpc);

  const signerWallet = new ethers.Wallet(privateKey, provider);
  const signerEOA = signerWallet.address;
  const aaAddress = sdk.getAccountAddress(signerEOA);
  
  console.log('--- Debug Info ---');
  console.log('Signer EOA:', signerEOA);
  console.log('AA Address:', aaAddress);

  // Check Balance
  const balance = await provider.getBalance(aaAddress);
  console.log('AA Balance:', ethers.formatEther(balance), 'KITE');

  // Check Deployed Status
  const code = await provider.getCode(aaAddress);
  const isDeployed = code.length > 2;
  console.log('Is Deployed?', isDeployed);

  if (isDeployed) {
    console.log('Account already deployed! Skipping deployment test.');
    return;
  }

  // Prepare Initialize Data
  const initializeCallData = ethers.Interface.from([
    'function initialize(address allowedToken, address owner)'
  ]).encodeFunctionData('initialize', [
    KITE_CONFIG.settlementToken,
    aaAddress
  ]);

  const proxyConstructorData = ethers.AbiCoder.defaultAbiCoder().encode(
    ['address', 'address', 'bytes'],
    [KITE_CONFIG.clientAgentVaultImpl, aaAddress, initializeCallData]
  );
  
  const transparentProxyBytecode = getTransparentProxyBytecode();
  const fullInitCode = transparentProxyBytecode + proxyConstructorData.slice(2);

  // Construct UserOp
  const deployRequest: UserOperationRequest = {
    target: aaAddress,
    value: 0n,
    callData: ethers.Interface.from([
      'function performCreate(uint256 value, bytes calldata initCode) returns (address)'
    ]).encodeFunctionData('performCreate', [0n, fullInitCode])
  };

  // Test 1: Estimate with default Paymaster (Expect Fail)
  console.log('\n--- Test 1: Estimating with Paymaster (Default) ---');
  try {
    const estimate = await sdk.estimateUserOperation(signerEOA, deployRequest);
    console.log('Estimation success:', estimate);
  } catch (error: any) {
    console.log('Expected Failure:', error.error?.message || error.message);
  }

  // Check if we can fund it
  if (balance === 0n) {
      console.log('\nAA Wallet has 0 KITE. Checking Signer funds...');
      const signerBalance = await provider.getBalance(signerEOA);
      console.log('Signer Balance:', ethers.formatEther(signerBalance));
      
      if (signerBalance > ethers.parseEther('0.02')) { // Fund with 0.01
             console.log('Funding 0.01 KITE to AA wallet...');
             const tx = await signerWallet.sendTransaction({
                 to: aaAddress,
                 value: ethers.parseEther('0.01')
             });
             console.log('Funding TX:', tx.hash);
             await tx.wait();
             console.log('Funding Confirmed.');
      } else {
             console.log('Signer has insufficient funds to fund AA. Cannot test raw deployment.');
      }
  } else {
      console.log('\nAA Wallet has funds. You can try manual deployment without Paymaster if SDK allows.');
  }
}

main().catch(console.error);
